import{useEffect,useState}from"react";export function useFetch(apiPath,fetcher){const[result,setResult]=useState({data:null,error:null,loading:!0});return useEffect(()=>{setResult(()=>({data:null,error:null,loading:!0}));const promise=fetcher(apiPath);promise(data=>{setResult(cur=>({...cur,data:data,loading:!1}))}),promise.catch(error=>{setResult(cur=>({...cur,error:error,loading:!1}))})},[apiPath,fetcher]),result}